import axios from 'axios'

export function authenticate(data, operation) {
    return new Promise((resolve, reject) => {
        let axiosConfig = {
            headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
            }
        }
        let content = {
            operation: operation,
            data: data
        }
        axios.defaults.withCredentials = true
        axios.post('http://localhost:8000/users/', content, axiosConfig)
            .then((response) => {
                console.log(response)
                window.open('/users')
                //resolve(response.data)
            })
            .catch((error) => {
                console.log(error)
                //reject(error)
            })
    })
}
