import Cover from '../../graphics/imageCover/Cover'
import Text from '../../graphics/textDisplayer/Text'

const images = {
    cover1: "images/Articles/cover-1.jpg",
    cover2: "images/Articles/cover-2.jpg",
    cover3: "images/Articles/cover-3.jpg",
    cover4: "images/Articles/cover-4.jpg",
    cover5: "images/Articles/cover-5.jpg",
    cover6: "images/Articles/cover-6.jpg",
    cover7: "images/Articles/cover-7.jpg",
    cover8: "images/Articles/cover-8.jpg",
    cover9: "images/Articles/cover-9.jpg",
}

function StorieCard({height, imageWidth,descriptionWidth, style }) {
    return (
        <div className={`${style} flex flex-row justify-center  ${height}`}>
            <Cover image={images.cover7} height={height} style={`flex justify-center items-center ${imageWidth} ${height}  overflow-hidden s`} imageStyle="transform hover:scale-105 translate-all ease-out duration-700">
            </Cover>
            <div className={`flex justify-center items-start right-8 ${descriptionWidth} h-full bg-yellow-400 `}>
                <Text text="stories" style={`h-full w-full font-abrilfatface text-bold text-7xl text-gray-900 opacity-75`} />
                <Text> </Text>
            </div>
        </div>
    )
}

export default function StoriesEntry({height, imageWidth, descriptionWidth, style}) {


    return (
        <div className={style}>
            <StorieCard height={height} imageWidth={imageWidth} descriptionWidth={descriptionWidth}/>
        </div>
    )
}
