import React, { useState } from 'react'
import WelcomeBar from './welcomeBar'
import CategoriesBar from './categoriesBar'
import { Icon } from 'react-icons-kit'
import {search, user, heart, cart } from 'react-icons-kit/icomoon'
import { ic_menu } from 'react-icons-kit/md/ic_menu'
import { cross } from 'react-icons-kit/icomoon/cross'
import Link from 'next/link'



/*
    categories : json contains our tags
    max_shown : number of tags displayed on NavBar "for others u need to press [more ... ]"
    links : linksss on the right side
    logo : path of the main logo

*/



function IconsContainer({ clickSearchIcon, searchIconClicked, style}) {

    const SearchIcon = () => (
        <form className="md:hidden flex justify-end h-8 w-full">
            <div className={`${searchIconClicked ? "order-last" : ""} flex justify-center h-full w-8 outline-none focus:outline-none`}
                onClick={clickSearchIcon}
            >
                <Icon icon={searchIconClicked ? cross : search}></Icon>
            </div>
            {searchIconClicked && <button className="h-full w-8 outline-none focus:outline-none"><Icon icon={search}></Icon></button>}
            <input className={searchIconClicked ? " h-full transform w-full transition-all ease-out duration-300  outline-none border-b border-gray-500" : "transform w-0 transition-all ease-out duration-300"} type="text" placeholder="search" ></input>
        </form>
    )

    const NewIcon = ({ size, icon }) => (
        <div className={`inline m-2 md:m-2 cursor-pointer text-gray-700`}>
            <Icon size={size} icon={icon}></Icon>
        </div>
    )

    return (
        <div className={style}>
            {!searchIconClicked && <div className="flex flex-row h-full">
                <SearchIcon />
                <NewIcon size={15} icon={user} />
                <span className="hidden lg:inline m-2" >user</span>

                <NewIcon size={15} icon={heart} />
                <span className="hidden lg:inline m-2" >favorites</span>
                <NewIcon size={15} icon={cart} />
                <span className="hidden lg:inline m-2" >{`(${0})`}</span>
            </div>}
        </div>
    )
}

function UpperNav({ changeMenuClicked, home, logo }) {

    const [searchIconClicked, setSearchIconClicked] = useState(false)

    const clickSearchIcon = () => {
        setSearchIconClicked(!searchIconClicked) // open/close the search component for small screens
    }

    const MenuContainer = ({style}) => (
        <div className={style}>
            <div className="m-1 md:hidden cursor-pointer text-gray-700"
                onClick={changeMenuClicked}
            >
                <Icon size={25} icon={ic_menu}></Icon>
            </div>
        </div>
    )

    const SiteNameContainer = ({style}) => (
        <div className={style}>
                <Link href={home}>
                    <a className="text-lg font-bold whitespace-no-wrap text-gray-900">Mor Made</a>
                </Link>
        </div>
    )

    return (
        <div className="flex w-full h-12">
            {!searchIconClicked && <MenuContainer style="flex items-center justify-start h-full flex-1" />}

            {!searchIconClicked && <SiteNameContainer style="flex justify-center items-center flex-1 h-full"/>}

            <IconsContainer style="flex flex-1 justify-center md:justify-end items-center  h-full w-full"
                searchIconClicked={searchIconClicked}
                clickSearchIcon={clickSearchIcon}
            />
        </div>
    )
}

function SearchContainer() {
    return (
        <form className="flex justify-end h-8">
            <button className="h-full w-8 outline-none  border-b border-gray-500 focus:outline-none"><Icon icon={search}></Icon></button>
            <input className="h-full w-24 outline-none border-b border-gray-500  transform transition-all ease-out duration-500 lg:focus:w-56" type="text" placeholder="search"></input>
        </form>
    )
}

function MiddleNav({ links }) {
    return (
        <div className="hidden md:flex h-12 w-full">
            <div className="h-full flex-1"></div> {/*white space container */}
            <ul className="flex flex-1 justify-center items-center h-full">
                {links.map((link, indx) =>
                    <li className="m-8 cursor-pointer text-base md:text-2xl font-worksans  text-gray-900 hover:text-gray-700" key={indx}>
                        <Link href={link.path}><a>{link.page}</a></Link>
                    </li>
                )}
            </ul>
            <div className="flex flex-1 justify-end items-center h-full">
                <SearchContainer />
            </div>
        </div>
    )
}

function NavBar({ changeMenuClicked, categories, max_shown, links, home }) {



    return (
        <nav className="fixed flex flex-col w-full bg-white" >
            <UpperNav
                changeMenuClicked={changeMenuClicked}
                home={home}
                links={links}
            />
            <MiddleNav
                links={links}
            />
            <WelcomeBar textToDisplay="welcome" />
            <CategoriesBar max_shown={max_shown} categories={categories} />
        </nav>
    )
}

export default NavBar
