import Cover from './article-widgets/Cover'
import Title from './article-widgets/Title'
import Description from './article-widgets/Description'

export default function ArticleContent ({image, text, title}) {
    return (
        <div className="flex justify-between w-full m-5 bg-yellow-200 border ">
            <Description text={text} style="flex justify-center items-center w-144 h-full p-5 shadow" />
            <Cover image={image} height="h-132" style="flex items-center w-132 h-132">
            </Cover>
        </div>
    )
}
