import styles from './styles/welcomeBar.module.css'

export default function WelcomeBar({textToDisplay}) {

    if (typeof window !== 'undefined') {
        var sticky_container = document.getElementById("sticky_container")
        console.log("hello ::::::: ", sticky_container.offsetTop)
        }
    return (
        <div id="sticky_container" className={styles.container}  style={{position : "fixed"}}>
            <div className={styles.welcome_container}>
                <h1>{textToDisplay}</h1>
            </div>
        </div>
    )
}
