import React, { useState, useRef } from 'react'
import Brand from '../logo/Brand'
import { Menu } from '../user'
import { NavMenu } from '.'

const Navbar = props => {

    const [menuDrop, setMenuDrop] = useState(false)

    const ref = useRef(null)

    const dropMenuOn = () => {
        console.log(menuDrop)
        if (menuDrop == true) {
            setMenuDrop(false)
        } else {
            setMenuDrop(true)
        }
    }

    const dropMenuOff = () => {
        setMenuDrop(false)
    }

    let content = (
        <div className={props.className}>
            <div className="fixed z-50 h-10 w-full flex bg-white">
                <div className="flex flex-row">
                    <Brand className="h-full w-auto ml-6" />
                </div>
                <div className="w-full flex flex-row-reverse">
                    <Menu className="h-full w-auto mr-6" ref={ref} username="ilyass" onClick={dropMenuOn}/>
                </div>
            </div>
            <NavMenu className="z-50" ref={ref} cookies={props.cookies} drop={menuDrop} callBack={dropMenuOff}/>
        </div>
    )

    return content
}

export default Navbar
