import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { parseCookies } from '../../lib/parseCookies'
import { Navbar } from '../../components/elements'
import { ProductGrid } from '../../components/widgets/product'

const UserPage = props => {

  console.log(props.cookies)

  const [data, setData] = useState();
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    console.log('useEffect runs');
    setIsLoading(true);
    axios.defaults.withCredentials = true
    axios.get('http://localhost:8000/products/')
      .then(res => {
        console.log(res.data)
        setData(JSON.stringify(res.data));
        setIsLoading(false);
      })
  }, []);

  let content = <p>Loading characters...</p>;

  if (!isLoading && data && data.length > 0) {
    //console.log(data[0])
    content = (
      <div className="w-full">
        <Navbar cookies={props.cookies} className="w-full" />
        <div className="w-1/1 h-auto grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-1 m-2">
          <ProductGrid className="h-auto w-auto m-2" data={JSON.parse(data)[0]} owner="ilyass" />
          <ProductGrid className="h-auto w-auto m-2" data={JSON.parse(data)[0]} owner="ilyass" />
          <ProductGrid className="h-auto w-auto m-2" data={JSON.parse(data)[0]} owner="ilyass" />
          <ProductGrid className="h-auto w-auto m-2" data={JSON.parse(data)[0]} owner="ilyass" />
          <ProductGrid className="h-auto w-auto m-2" data={JSON.parse(data)[0]} owner="ilyass" />
          <ProductGrid className="h-auto w-auto m-2" data={JSON.parse(data)[0]} owner="ilyass" />
          <ProductGrid className="h-auto w-auto m-2" data={JSON.parse(data)[0]} owner="ilyass" />
          <ProductGrid className="h-auto w-auto m-2" data={JSON.parse(data)[0]} owner="ilyass" />
        </div>
      </div>
    );
  } else if (!isLoading && (!data || data.length === 0)) {
    content = <p>Could not fetch any data.</p>;
  }
  return content;
};

UserPage.getInitialProps = ({ req }) => {
  const cookies = parseCookies(req);

  return {
    cookies: cookies
  };
};

export default UserPage;
