                    <StorieCard height="h-108" width="w-108" style=""/>
                    <StorieCard height="h-108" width="w-108" style=""/>
                </StorieCard>

            {/* </div> */}
            {/* <StorieCard height="h-120" width="w-7/12" /> */}

        </div>
        // <div className="flex flex-row justify-center w-full h-144 bg-red-100">
