import React, { useRef } from 'react'
import styles from './styles/welcomeBar.module.css'

export default function WelcomeBar({textToDisplay}) {

    const welcomeBar = useRef(null)
    return (
        <div ref={welcomeBar} className={styles.container}  style={(window.pageYOffset >= sticky)? {position : "fixed"}: {}}>
            {console.log("bar offset : ", sticky , " | window.pageYOffset : ", window.pageYOffset)}
            <div className={styles.welcome_container}>
                <h1>{textToDisplay}</h1>
            </div>
        </div>
    )
}
