import React, { useRef } from 'react'
import styles from './styles/welcomeBar.module.css'

export default function WelcomeBar({textToDisplay}) {


    return (
        <div className="hidden lg:relative lg:flex justify-center items-center w-full h-10 bg-gray-900 bg-opacity-50 sm:hidden overflow-hidden" style={{ backgroundImage : 'url("/images/Styles/style2.jpg")'}}>
            {/* <img className="absolute w-full object-cover object-center" src="/images/Styles/style0.jpg"/> */}
            <div className="flex items-center h-full text-center text-white ">
                <h1 >{ textToDisplay }</h1>
            </div>
        </div>
    )
}
