import React, { useState } from 'react'
import Brand from '../logo/Brand'
import { Menu } from '../user'
import { NavMenu } from '.'

const Navbar = props => {

    const [menuDrop, setMenuDrop] = useState(true)
    const [menuContent, setMenuContent] = useState(null)

    let menu = (
        <NavMenu className="z-50" cookies={props.cookies} drop={menuDrop} callBack={dropMenuOff}/>
    )

    const dropMenuOn = () => {
        console.log(menuDrop)
        if (menuDrop == true) {
            //setMenuDrop(false)
            setMenuContent(null)
        } else {
            //setMenuDrop(true)
            setMenuContent(menu)
        }
    }

    const dropMenuOff = () => {
        //setMenuDrop(false)
        setMenuContent(null)
    }

    let content = (
        <div className={props.className}>
            <div className="fixed z-50 h-10 w-full flex bg-white">
                <div className="flex flex-row">
                    <Brand className="h-full w-auto ml-6" />
                </div>
                <div className="w-full flex flex-row-reverse">
                    <Menu className="h-full w-auto mr-6" username="ilyass" onClick={dropMenuOn}/>
                </div>
            </div>
            {menuContent}
        </div>
    )

    return content
}

export default Navbar
