import Cover from './article-widgets/Cover'
import Title from './article-widgets/Title'
import Description from './article-widgets/Description'

export default function ArticleCover ({image, title, description}) {

    return (
        <div className="relative flex flex-col justify-start items-center w-full h-288 bg-yellow-300">
            <Title title={title} style="flex justify-center items-center w-64 h-32 font-bold text-3xl bg-white  text-gray-900"/>
            <Cover image={image} style="w-10/12 h-204 overflow-hidden bg-gray-400 shadow"/>
            <Description text={description} style="absolute bottom-4 w-6/12 h-56  bg-white" />
        </div>
    )
}
