import React, { useRef } from 'react'
import styles from './styles/welcomeBar.module.css'

export default function WelcomeBar({textToDisplay}) {


    return (
        <div className="hidden lg:relative lg:flex justify-center items-center w-full h-10 bg-gray-900 sm:hidden overflow-hidden" >
            <img className="w-full object-cover object-center opacity-25" src="/images/Styles/style1.jpg"/>
            <div className=" absolute flex justify-center items-center h-full w-full text-center font-bold text-white ">
                <h1>{ textToDisplay }</h1>
            </div>
        </div>
    )
}
