import Cover from '../../graphics/imageCover/Cover'
import Text from '../../graphics/textDisplayer/Text'
import Description from '../Article/article-utilities/article-widgets/Description'
import Link from 'next/link'

const images = {
    cover1: "images/Articles/cover-1.jpg",
    cover2: "images/Articles/cover-2.jpg",
    cover3: "images/Articles/cover-3.jpg",
    cover4: "images/Articles/cover-4.jpg",
    cover5: "images/Articles/cover-5.jpg",
    cover6: "images/Articles/cover-6.jpg",
    cover7: "images/Articles/cover-7.jpg",
    cover8: "images/Articles/cover-8.jpg",
    cover9: "images/Articles/cover-9.jpg",
}

function StorieCard({image, title, description, link, height, imageWidth, descriptionWidth, style }) {
    
    const styleFont = {
        titleStyle : "font-lobster font-xl",
        descriptionStyle : "font-lobster",
        linkStyle : "font-lobster"
    }
    return (
        <div className={`${style} flex flex-row justify-center  ${height}`}>
            <Cover image={image} height={height} style={`flex justify-center items-center ${imageWidth} ${height}  overflow-hidden s`} imageStyle="transform hover:scale-105 translate-all ease-out duration-700">
            </Cover>
            <div className={`flex flex-col justify-center items-center right-8 ${descriptionWidth} h-full bg-white`}>
            
                <Text text={title} style={`w-9/12 ${styleFont.titleStyle} text-gray-900`} />
                
                <Text text={description} style={`w-9/12 mt-2 mb-2 ${styleFont.descriptionStyle} text-gray-900 `} />
                
                <div className="w-9/12">
                <Link href={link.path}><a><Text text={link.text} style={`w-11/12 ${styleFont.linkStyle} text-gray-900 `}/></a></Link>
                </div>
x            
            </div>
        </div>
    )
}

export default function StoriesEntry({image, title, description, link, height, imageWidth, descriptionWidth, style}) {


    return (
        <div className={style}>
            <StorieCard 
                image={image}
                title={title}
                description={description}
                link={link}
                height={height}
                imageWidth={imageWidth}
                descriptionWidth={descriptionWidth}
                style="m-5" />
        </div>
    )
}
