import React, { useRef, useState, useEffect } from 'react'
import { authenticate } from '../../services/authentication/authentication'

const NavMenu = props => {

    let object = (
        <div className="">
            <div tabIndex="0" className="hover:bg-purple-600 w-24 border border-gray-900 bg-white">
                <button
                    className="focus:outline-none font-sans font-medium text-gray-900 text-sm ml-2"
                    type="button"
                    onClick={() => {authenticate({}, "logout", props.cookies['csrftoken'])}}>
                    logout
                </button>
            </div>
        </div>
    )

    /*
    const [subContent, setSubContent] = useState(() => {
        console.log(props.drop)
        if (props.drop == true) {
            return object
        } else {
            return null
        }
    })
    */

    let subContent = null

    let timeOutId = null

    const focusRef = useRef(null)

    const checkFocus = () => {
        if (focusRef.current.hasFocus()) {
            setSubContent(object)
        } else {
            setSubContent(null)
        }
    }

    const onBlurHandler = () => {
        setTimeout(() => {
            console.log('logout')
            props.callBack()
            //clearTimeout(timeOutId)
        }, 200);
    }

    /*
    useEffect(() => {
        console.log(props.drop)
        if (props.drop == true) {
            focusRef.current.focus();
        } 
        /* const checkLoop = setInterval(checkFocus, 200);
        return () => {
            clearInterval(checkLoop);
             onBlur={() => {setSubContent(null)}}
        }
    })*/

    useOnClickOutside(focusRef, onBlurHandler)

    let contentWithoutMenu = (
        <div className={props.className}>
            <div tabIndex="0" className="absolute top-0 right-0 mr-4 mt-10">
            </div>
        </div>
    )

    let contentWithMenu = (
        <div className={props.className} onBlur={onBlurHandler}>
            <div ref={focusRef} tabIndex="0" className="absolute top-0 right-0 mr-4 mt-10">
                {object}
            </div>
        </div>
    )

    if (props.drop == true) {
        return contentWithMenu
    } else {
        return contentWithoutMenu
    }
}

function useOnClickOutside(ref, handler) {
    useEffect(
      () => {
        const listener = event => {
          // Do nothing if clicking ref's element or descendent elements
          if (!ref.current || ref.current.contains(event.target)) {
            return;
          }
  
          handler(event);
        };
  
        document.addEventListener('mousedown', listener);
        document.addEventListener('touchstart', listener);
  
        return () => {
          document.removeEventListener('mousedown', listener);
          document.removeEventListener('touchstart', listener);
        };
      },
      // Add ref and handler to effect dependencies
      // It's worth noting that because passed in handler is a new ...
      // ... function on every render that will cause this effect ...
      // ... callback/cleanup to run every render. It's not a big deal ...
      // ... but to optimize you can wrap handler in useCallback before ...
      // ... passing it into this hook.
      [ref, handler]
    );
  }

export default NavMenu
