import Cover from './article-widgets/Cover'
import Title from './article-widgets/Title'
import Description from './article-widgets/Description'

export default function ArticleCover ({image, title, description}) {

    return (
        <div className="relative flex flex-col justify-start items-center w-full h-228 bg-yellow-300">
            <Title title={title} style="flex justify-center items-center w-64 h-24 font-bold text-3xl bg-white border text-gray-900"/>
            <Cover image={image} style="w-11/12 h-180 overflow-hidden bg-gray-400"/>
            <Description text={description} style="absolute bottom-8 w-8/12 h-56 border bg-white" />
        </div>
    )
}
