import axios from 'axios';
import querystring from 'querystring';
export function registerUser(data, operation, csrttoken) {
  return new Promise(function (resolve, reject) {
    var axiosConfig = {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'X-CSRFToken': csrttoken
      }
    };
    var content = {
      operation: operation,
      data: querystring.stringify(data)
    };
    axios.defaults.withCredentials = true;
    axios.post(process.env.domain + '/api/users/', querystring.stringify(content), axiosConfig).then(function (response) {
      console.log(response.data);
      localStorage.setItem('_maker', JSON.stringify(response.data));
      resolve(response.data);
    })["catch"](function (error) {
      reject(error);
    });
  });
}
