import React, { useRef } from 'react'

const AddImage = props => {

    const inputRef = useRef(null)

    /*
    const img_pathUrl = (input) => {
        console.log(input.target.files)
        imgRef.current.src = (window.URL ? URL : webkitURL).createObjectURL(input.target.files[0]);
    }
    */

    let content = (
        <div>
            <div>
                <input
                    className="invisible"
                    ref={inputRef}
                    accept="image/jpeg, image/jpg, image/png"
                    multiple
                    type="file"
                    onChange={(input) => props.onChange(input)}
                />
                <div className="h-32 w-32 content-center border border-1 border-gray-900 hover:bg-xw-200 focus:outline-none" tabIndex="0" role="button" onClick={() => { inputRef.current.click() }}>
                    <span className="" dir="auto">
                        Upload Photo
                    </span>
                </div>
            </div>
        </div>
    )

    return content
}

export default AddImage
