import React, { useState } from 'react'
import Brand from '../logo/Brand'
import { Menu } from '../user'
import { NavMenu } from '.'

const Navbar = props => {

    const [menuDrop, setMenuDrop] = useState(false)

    const dropMenuOn = () => {
        setMenuDrop(true)
    }

    const dropMenuOff = () => {
        setMenuDrop(false)
    }

    let content = (
        <div className={props.className}>
            <div className="h-8 w-full flex bg-xw-200">
                <div className="flex flex-row bg-green-600">
                    <Brand className="h-full w-auto ml-4 bg-blue-600" />
                </div>
                <div className="w-full flex flex-row-reverse bg-yellow-600">
                    <Menu className="h-full w-auto bg-red-600 mr-4" username="ilyass" onClick={dropMenuOn}/>
                </div>
            </div>
            <NavMenu drop={menuDrop} callBack={dropMenuOff}/>
        </div>
    )

    return content
}

export default Navbar
