import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import { AddProduct } from '../components/panel'
import { Login, SignUp } from '../components/authentication'
import { parseCookies } from '../lib/parseCookies'
import Cookie from 'js-cookie'
//import '../src/tailwind.generated.css'

const App = () => (
  <div className="max-w-md mx-auto flex p-6 bg-gray-100 mt-10 rounded-sm shadow-sm">
    <div className="ml-1 pt-1">
      <div class="md:flex rounded-lg">
        <img class="h-16 w-16 md:h-24 md:w-24 rounded-full mx-auto md:mx-0 md:mr-6" src="avatar.jpg" />
        <div class="text-center md:text-left">
          <h2 class="text-lg">Morty Squanchers</h2>
          <div class="text-purple-500">Customer Support</div>
          <div class="text-gray-600">morty@rick.com</div>
          <div class="text-gray-600">(555) 765-4321</div>
        </div>
      </div>
    </div>
  </div>
)

const Navbar = () => (
  <div class="pt-2 px-2 relative mx-auto text-gray-600 md:flex">
    <h2 class="text-lg px-2">Mormade</h2>
    <input class="border border-gray-300 bg-white h-10 px-5 pr-16 rounded-lg text-sm focus:outline-none"
      type="search" name="search" placeholder="Search" />
    <button type="submit" class="absolute right-0 top-0 mt-5 mr-4">
    </button>
  </div>
)

const Auth = ({ cookies = {}}) => {

  console.log(cookies)

  const [content, setContent] = useState()

  let contentDOM = (
    <div className="m-6">
      <button className="border border-solid border-black" value="Login" type="button" onClick={() => setContent(Login)} >Login</button>
      <button value="Sign Up" type="button" onClick={() => setContent(SignUp)} >Sign Up</button>
      <div>{content}</div>
    </div>
  )

  return contentDOM
}

Auth.getInitialProps = ({ req }) => {
  const cookies = parseCookies(req);

  return {
    cookies: cookies
  };
};

export default Auth

//ReactDOM.render(<App />, document.querySelector('#root'))
