import React, { useRef } from 'react'
import { ImgRound } from '../graphics/images'

const Menu = props => {

    const focusRef = useRef(null);

    console.log('hahahah')

    const onMenuClick = () => {
        console.log(focusRef.current)
        focusRef.current.focus();
    };

    let content = (
        <div className={props.className}>
            <div className="h-8 w-auto container content-center mx-auto flex flex-wrap">
                <ImgRound className="w-5 h-5" src="/profile.png" />
                <button className="focus:outline-none" type="button" onClick={onMenuClick}>
                    <p className="font-sans font-medium text-sm text-gray-900 text-center ml-2">
                        {props.username}
                    </p>
                </button>
            </div>
            <div ref={focusRef} tabIndex="1" className="absolute invisible focus:visible hover:bg-purple-600 w-24 top-0 right-0 border border-gray-900 bg-white mr-4 mt-10">
                <button className="focus:outline-none font-sans font-medium text-gray-900 text-sm ml-2">
                    logout
                </button>
            </div>
        </div>
    )

    return content
}

export default Menu
