import React, { useRef } from 'react'

const SelectCategory = props => {

    const optionRef = useRef()

    const options = props.data.map((option) => {
        return <option key={option.id} value={option.value} />
    })

    const getOption = () => {
        let option = optionRef.current.option
        props.setCategory(option.key, option.value, props.lvl)
    }

    let content = (
        <div className={props.className}>
            <input ref={optionRef} list="data" onChange={getOption}/>
            <datalist id="data">
                {options}
            </datalist>
        </div>
    )

    return content
}

export default SelectCategory
