import React, { useRef, useState, useEffect } from 'react'

const NavMenu = props => {

    let object = (
        <div className="">
            <div tabIndex="0" className="hover:bg-purple-600 w-24 border border-gray-900 bg-white">
                <button className="focus:outline-none font-sans font-medium text-gray-900 text-sm ml-2">
                    logout
                </button>
            </div>
        </div>
    )

    const [subContent, setSubContent] = useState(() => {
        console.log(props.drop)
        if (props.drop == true) {
            return object
        } else {
            return null
        }
    })

    const focusRef = useRef(null)

    const checkFocus = () => {
        if (focusRef.current.hasFocus()) {
            setSubContent(object)
        } else {
            setSubContent(null)
        }
    }

    useEffect(() => {
        if (props.drop == true) {
            setSubContent(object)
        } else {
            setSubContent(null)
        }
        console.log(focusRef.current)
        focusRef.current.focus();
        /* const checkLoop = setInterval(checkFocus, 200);
        return () => {
            clearInterval(checkLoop);
        }*/
    })

    let content = (
        <div className={props.className} onBlur={() => {setSubContent(null)}}>
            <div ref={focusRef} tabIndex="0" className="absolute top-0 right-0 mr-4 mt-10">
                {subContent}
            </div>
        </div>
    )

    return content
}

export default NavMenu
