    {
        tag: "furniture",
        sous_tags: ["tables", "beds", "chair", "sofa", "wardrobes"],
        tag_image: "images/tags/furniture/furniture-1.jpg"
    },
    {
        tag: "gifts",
        sous_tags: ["parfum", "braclet", "socks", "backpack", "necklace", "wallet"],
        tag_image: "images/tags/gifts/gifts-1.jpg"
    }
]

const links = [
    {
        page: 'MAKERS',
        path: '/makers'
    },
    {
        page: 'REGIONS',
        path: '/regions'

    },
    {
        page: 'STORIES',
        path: '/stories'
    }
]


function BlackFocusScreen({display, onClick}) {
    <div 
        className={display ? "absolute top-0 left-0 w-full h-full transition ease-out duration-500 bg-gray-900 bg-opacity-50" : "hidden bg-opacity-"}
        onClick={onClick}
    >
    </div>
}


function Layout({ children }) {
    const clickMenuIcon = () => {
        setMenuClicked(!menuClicked)
    }

    const [menuClicked, setMenuClicked] = useState(false)    
    return (
        <div>

            <SideMenu
                menuClick={menuClicked}
                changeMenuClicked={clickMenuIcon}
            />

            <div 
                className={menuClicked ? "absolute top-0 left-0 w-full h-full transition ease-out duration-500 bg-gray-900 bg-opacity-50" : "hidden bg-opacity-"}
                onClick={clickMenuIcon}
            >
            </div>

            <NavBar
                changeMenuClicked={clickMenuIcon}
                logo="/logo.png"
                categories={categories}
                max_shown={8}
                links={links}
                home="/"
            />
            
            {children}

        </div>
    )
}

export default Layout
