    }
]




function Layout({ children }) {
    const clickMenuIcon = () => {
        setMenuClicked(!menuClicked)
    }

    const [menuClicked, setMenuClicked] = useState(false)    
    return (
        <div>
            <SideMenu menuClick={menuClicked} changeMenuClicked={clickMenuIcon} />
            <div className="absolute top-0 left-0 w-full h-full bg-gray-700 bg-opacity-50" ></div>
            <NavBar changeMenuClicked={clickMenuIcon} logo="/logo.png" categories={categories} max_shown={8} links={links} home="/" />
