import React, { useState, useEffect } from 'react'
import { AddImage, RemovedSquaredImg } from '../../graphics/images'

const AddImageGrid = props => {

    const [files, setFiles] = useState([])
    const [imagesList, setImagesList] = useState(null)

    useEffect(() => {
        console.log('effect')
        if (files.length > 0) {
            imagesList = files.map(function(file){
                imgSrc = (window.URL ? URL : webkitURL).createObjectURL(file)
                return <RemovedSquaredImg className="w-32 h-32" src={imgSrc}/>
              })
        }
    }, [])

    const appendFile = (newFiles) => {
        console.log(newFiles)
        newFiles.forEach((f) => files.push(f), this)
        setFiles(files)
        console.log(files)
        if (files.length > 0) {
            setImagesList(files.map(function(file){
                const imgSrc = (window.URL ? URL : webkitURL).createObjectURL(file)
                return <RemovedSquaredImg className="w-32 h-32" src={imgSrc}/>
              }))
        }
    }

    let content = (
        <div>
            <div className="h-auto w-full container flex content-start flex-wrap">
                {imagesList}
                <AddImage onChange={appendFile}/>
            </div>
        </div>
    )

    return content
}

export default AddImageGrid
