import Cover from './article-widgets/Cover'
import Title from './article-widgets/Title'
import Description from './article-widgets/Description'

export default function ArticleCover ({image, title, description}) {

    return (
        <div className=" flex flex-col justify-start items-center w-full h-288 ">
            <Title title={title} style="flex justify-center items-center w-64 h-32 font-bold text-3xl"/>
            <Cover image={image} style="w-full h-204 overflow-hidden bg-gray-400 shadow-sm"/>
            <Description text={description} style="m-10 w-6/12 h-72 bg-white shadow-sm" />
        </div>
    )
}
