import Cover from './article-widgets/Cover'
import Title from './article-widgets/Title'
import Description from './article-widgets/Description'

export function ArticleContent ({image, text, title}) {
    return (
        <div className="flex flex-col justify-center items-center mt-24 w-full ">
            <Description text={text} style="flex justify-center items-center p-20  w-144 h-full  " />
            <Cover image={image} height="h-180" style="flex items-center  mr-10 w-180 h-132 rounded-sm overflow-hidden shadow">
            </Cover>
        </div>
    )
}

export function ArticleContent1 ({image, text, title}) {
    return (
        <div className="flex flex-row justify-end mt-24 w-full h-132 ">
            <Description text={text} style="flex justify-center items-center p-20  w-144 h-full  " />
            <Cover image={image} height="h-180" style="flex items-center  mr-10 w-180 h-132 rounded-sm overflow-hidden shadow">
            </Cover>
        </div>
    )
}
