import React, { useState, useEffect } from 'react'
import useSWR from 'swr'
import SelectCategory from '../../graphics/category/SelectCategory'
import { getCategories } from '../../../services/api/fetch/getCategories'
import { RemovableTag } from '../../graphics/tags'

const AddCategory = props => {

    const [categoriesList, setCategoriesList] = useState([])
    const [lvl, setLvl] = useState(0)
    let parentId = null
    let categories = []

    const setCategory = (category) => {
        categories.splice(category.lvl, 0, category)
        console.log(categories)
        parentId = category.id
        setLvl(1)
        setCategoriesList([])
        initList()
    }

    const initList = () => {
        if (categories.length > 0) {
            setCategoriesList(categories.map(function (category) {
                console.log(lvl)
                if (category.lvl < lvl) {
                    return (
                        <div className="flex">
                            <RemovableTag category={category} />
                        </div>
                    )
                }
            }))
        } else {
            setCategoriesList(null)
        }
    }

    useEffect(() => {
        initList()
    }, [])

    let content = (
        <div className={props.className}>
            <div className="w-full">
                <label className="block mb-1 font-sans font-medium">Add Product Category</label>
                <div className="h-auto w-full container content-start content-center mx-auto flex flex-wrap py-2 px-3">
                    {categoriesList}
                    <SelectCategory lvl={lvl} parent={parentId} csrftoken={props.csrftoken} setCategory={setCategory} />
                </div>
            </div>
        </div>
    )

    return content
}

export default AddCategory
