import Cover from './article-widgets/Cover'
import Title from './article-widgets/Title'
import Description from './article-widgets/Description'

export default function ArticleContent ({image, text, title}) {
    return (
        <div className="flex flex-row w-full h-132 ">
            <Description text={text} style="flex flex-1 justify-center items-start w-144  h-full p-20 shadow" />
            <Cover image={image} height="h-180" style="flex flex-1 items-center w-180 h-132">
            </Cover>
        </div>
    )
}
