import React, { useState, useEffect, useRef } from 'react'
import useSWR from 'swr'
import { getCategories } from '../../../services/api/fetch/getCategories'

const SelectCategory = props => {

    const selectedRef = useRef()
    const [data, setData] = useState()
    const [lvl, setLvl] = useState()
    const [parent, setParent] = useState(null)

    let content = null

    useEffect(() => {
        console.log('props.lvl')
        setLvl(props.lvl)
        console.log(lvl)
        getCategories(props.lvl, props.parent, props.csrftoken).then((res) => {
            console.log(res)
            setData(JSON.stringify(res))
        })
    }, [])

    if (data && JSON.parse(data).length > 0) {
        const options = JSON.parse(data).map((option) => {
            return <option key={option.id} value={option.name} />
        })

        const getOption = () => {
            let value = selectedRef.current.value
            JSON.parse(data).map((option) => {
                if (option.name == value) {
                    props.setCategory(option)
                    setData(null)
                }
            })
        }

        content = (
            <div className={props.className}>
                <input className="h-auto w-auto font-sans font-medium border text-gray-700 leading-tight hover:outline-none focus:outline-none p-1" ref={selectedRef} list="data" onChange={getOption} />
                <datalist id="data">
                    {options}
                </datalist>
            </div>
        )
    }

    return content
}

export default SelectCategory
