import { authenticate } from '../../services/authentication/authentication'

const Login = () => {

    console.log('enter login')

    let email;
    let password;

    const handleEmailChange = event => {
        email = event.target.value
    }

    const handlePasswordChange = event => {
        password = event.target.value
    }

    const handleSubmit = event => {
        event.preventDefault()
        console.log('********')
        const data = {
            email: email,
            password: password,
        }
        console.log('__________')
        authenticate(data, "login").then((response) => {
            if (response.status === "error") {
                console.log('error')
            }
            else {
                console.log('success')
            }
        })
            .catch((error) => {
                console.log(error)
            })
    }

    let content = (
        <div className="container mx-auto mx-w-3xl h-full flex bg-gray-100">
            <div className="m-12">
                <div className="">
                <h3 className="w-full font-sans font-bold text-xl text-gray-900 text-center mt-3 mb-2">Sign in</h3>
                <h4 className="w-full font-sans font-medium text-sm text-gray-800 text-center mt-3 mb-10">Welcome dear maker</h4>
                <div className="">
                    <form className="" action="">
                        <div className="mb-3 mt-2">
                            <label className="block mb-1">Email</label>
                            <input className="form-input w-48" type="text" onChange={handleEmailChange} name="email" />
                        </div>
                        <div className="mb-3 mt-2">
                            <label className="block mb-1">Password</label>
                            <input type="password" onChange={handlePasswordChange} name="password" />
                        </div>
                        <input
                            className="w-full bg-gray-800 hover:bg-grey-900 text-white text-sm py-2 px-4 mt-4 mb-4"
                            type="submit"
                            value="Login"
                            onClick={handleSubmit}
                        />
                    </form>
                </div>
                </div>
            </div>
        </div>
    )

    return content;
}

export default Login
