import React, { useRef } from 'react'
import styles from './styles/welcomeBar.module.css'

export default function WelcomeBar({textToDisplay}) {


    return (
        <div className="hidden lg:flex justify-center items-center w-full h-10 bg-gray-900 sm:hidden">
            <div className="flex items-center h-full text-center text-white bg-red-600">
                <h1 className="align-top bg-green-700">{ textToDisplay }</h1>
            </div>
        </div>
    )
}
