import React, { useState, useEffect } from 'react'
import useSWR from 'swr'
import SelectCategory from '../../graphics/category/SelectCategory'
import { getCategories } from '../../../services/api/fetch/getCategories'

const AddCategory = props => {

    const [categoriesList, setCategoriesList] = useState(null)

    let categories = []
    let lvl = 0

    const setCategory = (id, value, categoryLvl) => {
        categories.splice(categoryLvl, 0, id)
        console.log(value)
        lvl = categoryLvl + 1
    }

    const initList = () => {
        if (categories.length > 0) {
            setCategoriesList(categories.map(function (category) {
                return <SelectCategory lvl={category.lvl} parent={(category.lvl == 0) ? null : categories[category.lvl - 1].id} csrftoken={props.csrftoken} setCategory={setCategory} />
            }))
        } else {
            setCategoriesList(null)
        }
    }

    let content = (
        <div className={props.className}>
            <div className="w-full">
                <label className="block mb-1 font-sans font-medium">Add Product Category</label>
                <div className="h-auto w-full container flex content-start flex-wrap py-2 px-3">
                    <SelectCategory lvl={lvl} parent={(lvl == 0) ? null : categories[lvl - 1].id} csrftoken={props.csrftoken} setCategory={setCategory} />
                </div>
            </div>
        </div>
    )

    return content
}

export default AddCategory
