import { authenticate } from '../../services/authentication/authentication'

const Login = () => {

    console.log('enter login')

    let email;
    let password;

    const handleEmailChange = event => {
        email = event.target.value
    }

    const handlePasswordChange = event => {
        password = event.target.value
    }

    const handleSubmit = event => {
        event.preventDefault()
        console.log('********')
        const data = {
            email: email,
            password: password,
        }
        console.log('__________')
        authenticate(data, "login").then((response) => {
            if (response.status === "error") {
                console.log('error')
            }
            else {
                console.log('success')
            }
        })
            .catch((error) => {
                console.log(error)
            })
    }

    let content = (
        <div className="container">
            <h3 className="p-1 font-sans font-bold text-xl text-gray-900">Sign in</h3>
            <h4 className="p-1 font-sans font-medium text-sm text-gray-800">Welcome dear maker</h4>
            <div>
                <form className="flex items-center justify-center" action="">
                    <label className="">Email</label>
                    <input type="text" onChange={handleEmailChange} name="email" />
                    <label>Password</label>
                    <input type="password" onChange={handlePasswordChange} name="password" />
                    <input type="submit" value="Login" onClick={handleSubmit} />
                </form>
            </div>
        </div>
    )

    return content;
}

export default Login
