import React, { useState } from 'react'
import { Icon } from 'react-icons-kit'
import {search, user, heart, cart } from 'react-icons-kit/icomoon'
import { ic_menu } from 'react-icons-kit/md/ic_menu'
import { cross } from 'react-icons-kit/icomoon/cross'
import Link from 'next/link'


function IconsContainer({ openSearch, openSearchClickHandler, style}) {

    const SearchIcon = () => (
        <form className="md:hidden flex justify-end h-8 w-full">
            <div className={`${openSearch ? "order-last" : ""} flex justify-center h-full w-8 outline-none focus:outline-none`}
                onClick={openSearchClickHandler}
            >
                <Icon icon={openSearch? cross : search}></Icon>
            </div>
            {openSearch && <button className="h-full w-8 outline-none focus:outline-none"><Icon icon={search}></Icon></button>}
            <input className={openSearch ? " h-full transform w-full transition-all ease-out duration-300  outline-none border-b border-gray-500" : "transform w-0 transition-all ease-out duration-300"} type="text" placeholder="search" ></input>
        </form>
    )

    const NewIcon = ({ size, icon }) => (
        <div className={`inline m-2 md:m-2 cursor-pointer text-gray-700`}>
            <Icon size={size} icon={icon}></Icon>
        </div>
    )

    return (
        <div className={style}>
            {!openSearch && <div className="flex flex-row h-full">
                <SearchIcon />
                <NewIcon size={15} icon={user} />
                <span className="hidden lg:inline m-2" >user</span>

                <NewIcon size={15} icon={heart} />
                <span className="hidden lg:inline m-2" >favorites</span>
                <NewIcon size={15} icon={cart} />
                <span className="hidden lg:inline m-2" >{`(${0})`}</span>
            </div>}
        </div>
    )
}

function UpperNav({changeMenuClicked, home, logo }) {

    const [openSearch, setOpenSearch] = useState(false) // search icon for small screens -> expanded or closed

    const openSearchClickHandler = () => {
        setOpenSearch(!openSearch) // open/close the search component for small screens
    }

    const MenuContainer = ({style}) => (
        <div className={style}>
            <div className="m-1 md:hidden cursor-pointer text-gray-700"
                onClick={changeMenuClicked}
            >
                <Icon size={25} icon={ic_menu}></Icon>
            </div>
        </div>
    )

    const SiteNameContainer = ({style}) => (
        <div className={style}>
                <Link href={home}>
                    <a className="text-lg font-bold whitespace-no-wrap text-gray-900">Mor Made</a>
                </Link>
        </div>
    )

    return (
        <div className="flex w-full h-12">
            {!openSearch && <MenuContainer style="flex items-center justify-start h-full flex-1" />}

            {!openSearch && <SiteNameContainer style="flex justify-center items-center flex-1 h-full"/>}

            <IconsContainer style="flex flex-1 justify-center md:justify-end items-center  h-full w-full"
                openSearch={openSearch}
                openSearchClickHandler={openSearchClickHandler}
            />
        </div>
    )
}

export default UpperNav
