import React from 'react'

const Btn = props => {

    let borderColor = 'black'
    let textColor = 'white'
    let textSize = 'sm'
    let textWeight = 'medium'
    let hoverColor = 'gray-700'
    let bgColor = 'black'
    let height = 12
    let shadow = 'border border-'

    if (props.height != null) {
        height = props.height
    }

    if (props.borderColor != null) {
        borderColor = props.borderColor
    }

    if (props.textColor != null) {
        textColor = props.textColor
    }

    if (props.textSize != null) {
        textColor = props.textSize
    }

    if (props.hoverColor != null) {
        hoverColor = props.hoverColor
    }

    if (props.bgColor != null) {
        bgColor = props.bgColor
    }

    if (props.shadow == true) {
        shadow = 'shadow'
        borderColor = ''
    }

    if (props.textWeight != null) {
        textWeight = props.textWeight
    }
    
    const content = (
        <div className={props.className}>
            <button
                className={'w-full h-full bg-' + bgColor + ' hover:bg-' + hoverColor + ' focus:outline-none focus:border-' + hoverColor + ' font-roboto font-' + textWeight + ' text-' + textColor + ' text-' + textSize + ' py-2 px-4'}
                type="button"
                onClick={props.onClick}>
                    {props.value}
            </button>
        </div>
    )

    return content
}

export default Btn
