import Cover from '../../graphics/imageCover/Cover'
import Text from '../../graphics/textDisplayer/Text'

export default function AlphaDisplayer({children, style, heigth, width, image, title, description, buttonText, clickAction}) {
    return (
        <div className={style}>
            <Cover image={image} height={heigth} style={`flex flex-col justify-center items-center ${heigth} ${width}`}>
                <Text text={title} style="m-2" textStyle="text-white text-xl font-bold"/>
                <Text text={description} style="m-1" textStyle="text-white"/>
                <button className="h-12 w-40 bg-white text-gray-900">{buttonText}</button>
            </Cover>
            {children}
        </div>
    )
}
