import React, { useRef } from 'react'
import useSWR from 'swr'
import { getCategories } from '../../../services/api/fetch/getCategories'

const SelectCategory = props => {

    const optionsRef = useRef()
    const selectedRef = useRef()

    const { data, error } = useSWR('categories', getCategories(props.lvl, props.parent, props.csrftoken))

    if (!data) return <h3>loading ...</h3>

    const options = JSON.parse(data).map((option) => {
        return <option key={option.id} value={option.name} />
    })

    const getOption = () => {
        let value = selectedRef.current.value
        JSON.parse(data).map((option) => {
            if (option.name == value) {
                props.setCategory(option.id, option.value, props.lvl)
            }
        })
    }

    let content = (
        <div className={props.className}>
            <div className="border border-1 p-1">
                <input className="font-sans font-medium text-gray-700 leading-tight hover:outline-none focus:outline-none" ref={selectedRef} list="data" onChange={getOption} />
                <datalist id="data">
                    {options}
                </datalist>
            </div>
        </div>
    )

    return content
}

export default SelectCategory
