                if (response.status === "error") {
                    console.log('error')
                }
                else {
                    console.log('success')
                }
            })
                .catch((error) => {
                    console.log(error)
                })
        }
    }
    let content = (
        <div>
            <h3>Login</h3>
            <div>
                <form action="">
                    <label>Email</label>
                    <input type="text" onChange={handleEmailChange} name="email"  />
                    <label>Password</label>
                    <input type="password" onChange={handlePasswordChange} name="password"  />
                    <input type="submit" value="Login" />
                </form>
            </div>
        </div>
    )
}

export default Login
