import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { parseCookies } from '../../lib/parseCookies'
import { authenticate } from '../../services/authentication/authentication'

const UserPage = props => {

  console.log(props.cookies)

  const [data, setData] = useState();
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    console.log('useEffect runs');
    setIsLoading(true);
    axios.defaults.withCredentials = true
    axios.get('http://localhost:8000/products/')
      .then(res => {
        console.log(res.data)
        setData(JSON.stringify(res.data));
        setIsLoading(false);
      })
  }, []);

  const handleSubmit = event => {
    event.preventDefault()
    console.log('****logout****')
    const data = {}
    console.log('_____logout_____')
    authenticate(data, "logout", cookies.csrftoken).then((response) => {
      if (response.status === "error") {
        console.log('error')
      }
      else {
        console.log('success')
      }
    })
      .catch((error) => {
        console.log(error)
      })
  }

  let content = <p>Loading characters...</p>;

  if (!isLoading && data && data.length > 0) {
    console.log(data)
    content = (
      <div>
        <p>
          {data}
        </p>
        <button type="button" onClick={handleSubmit}>logout</button>
      </div>
    );
  } else if (!isLoading && (!data || data.length === 0)) {
    content = <p>Could not fetch any data.</p>;
  }
  return content;
};

UserPage.getInitialProps = ({ req }) => {
  const cookies = parseCookies(req);

  return {
    cookies: cookies
  };
};

export default UserPage;
