import {Cover1} from '../../graphics/imageCover/Cover'
import Text from '../../graphics/textDisplayer/Text'

export default function AlphaDisplayer({children, style, height, width, image, title, description, buttonText, clickAction}) {
    
    const textStyle = {
        titleStyle: "font-kumbhsans font-bold text-2xl text-center break-words text-white",
        descriptionStyle: " text-base text-center text-white",
        buttonStyle: "font-yantramanav text-sm font-bold"
    }
    
    return (
        <div className="relative flex  overflow-hidden my-5 mx-5 bg-gray-900">
            <img className="object-cover object-center" src={image} alt="image cover" />
            <div className="absolute flex flex-col justify-end items-center w-full h-full b">
                {/* <div className="flex flex-col justify-center items-center bg-white bg-opacity-75  w-10/12 h-3/12 "> */}
                    <h2 className={textStyle.titleStyle}>{title}</h2>
                    <p className={textStyle.descriptionStyle}>{description}</p>
                    <div className="flex justify-center items-center px-5 py-2 bg-white "><p className={textStyle.buttonStyle}>{buttonText}</p></div>                
                {/* </div> */}
            </div>
        </div>
    )
}
