import Cover from './article-widgets/Cover'
import Title from './article-widgets/Title'
import Description from './article-widgets/Description'

export default function ArticleContent ({image, text, title}) {
    return (
        <div className="flex flex-row justify-end mt-5 w-full h-132 ">
            <Description text={text} style="flex justify-center items-start w-144  h-full p-20 shadow-xl " />
            <Cover image={image} height="h-180" style="flex items-center ml-10 mr-10 w-180 h-132">
            </Cover>
        </div>
    )
}
