import react, { useState, useEffect } from 'react'
import { getCategories } from '../../../services/api/fetch/getCategories'
import { BlackTag } from '../../graphics/tags'

const DisplayCategories = props => {

    const [tags, setTags] = useState([])

    useEffect(() => {
        for (let i = 0; i < props.tags.length; i++) {
            getCategories(id, null, null, props.csrf).then((tag) => {
                let nTags = tags
                nTags.push(tag)
                setTags(nTags)
            })
        }
    }, [])

    let content = (
        <div className={props.className} >
            <div className="h-auto w-full flex flex-wrap">
                {JSON.parse(tags).map((tag) => {
                    <BlackTag className="" tag={tag}/>
                })}
            </div>
        </div>
    )

    return content
}

export default DisplayCategories
