import { authenticate } from '../../services/authentication/authentication'
import { btnBBW } from '../graphics/buttons'

const Login = () => {

    console.log('enter login')

    let email;
    let password;

    const handleEmailChange = event => {
        email = event.target.value
    }

    const handlePasswordChange = event => {
        password = event.target.value
    }

    const handleSubmit = event => {
        event.preventDefault()
        console.log('********')
        const data = {
            email: email,
            password: password,
        }
        console.log('__________')
        authenticate(data, "login").then((response) => {
            if (response.status === "error") {
                console.log('error')
            }
            else {
                console.log('success')
            }
        })
            .catch((error) => {
                console.log(error)
            })
    }

    let content = (
        <div className="container mx-auto w-full max-w-lg bg-xw-100">
            <div className="p-12 w-full">
                <h3 className="w-full font-sans font-bold text-xl text-gray-900 text-center mb-2">Sign in</h3>
                <h4 className="w-full font-sans font-medium text-sm text-gray-800 text-center mt-3 mb-10">Welcome dear maker</h4>
                <div className="">
                    <form className="" action="">
                        <div className="mb-3 mt-2">
                            <label className="block mb-1 font-sans">Email</label>
                            <input
                                className="appearance-none border w-full h-12 py-2 px-3 font-sans font-medium text-gray-700 leading-tight focus:outline-none focus:border-black"
                                type="text"
                                onChange={handleEmailChange}
                                name="email"
                            />
                        </div>
                        <div className="mb-3 mt-2">
                            <label className="block mb-1 font-sans font-medium">Password</label>
                            <input
                                className="appearance-none border w-full h-12 py-2 px-3 font-sans font-medium text-gray-700 leading-tight focus:outline-none focus:border-black"
                                type="password"
                                onChange={handlePasswordChange}
                                name="password"
                            />
                        </div>
                        <input
                            className="w-full h-12 bg-gray-900 hover:bg-gray-700 font-sans font-medium text-white text-sm py-2 px-4 mt-4 mb-4"
                            type="button"
                            value="SIGN IN"
                            onClick={handleSubmit}
                        />
                        <input
                            className="w-full h-12 bg-xw-100 border border-black hover:bg-xw-200 font-sans font-medium text-gray-900 text-sm py-2 px-4 mt-3 mb-4"
                            type="button"
                            value="REGISTER AS MAKER"
                            onClick={() => {}}
                        />
                        <btnBBW value="DO NOTHING" width="w-full" onClick={() => {}}/>
                        <input
                            className="w-full h-12 bg-xw-100 border border-black hover:bg-xw-200 font-sans font-medium text-gray-900 text-sm py-2 px-4 mt-3 mb-4"
                            type="button"
                            value="REGISTER AS MAKER"
                            onClick={() => {}}
                        />
                    </form>
                </div>
            </div>
        </div>
    )

    return content;
}

export default Login
