        authenticate(data, "login", props.csrf).then((response) => {
            if (response.status === "error") {
                console.log('error')
            }
            else {
                console.log('success')
            }
        })
            .catch((error) => {
                console.log(error)
            })
    }

    let content = (
        <div className={props.className}>
            <div className="relative w-full h-auto">
                <img className="absolute h-auto object-cover" src="/wheel-pot.jpg" />
                <div className="absolute w-full h-auto p-4">
                    <h3 className="h-auto w-full text-white text-xl text-right align-right mb-6">صانع ؟ إنضم إلينا</h3>
                    <div className="w-full h-auto flex flex-wrap justify-end items-end">
                        <div className="h-full flex-1/2 content-end flex-wrap">
                            <BtnBbw className="w-auto h-12 mr-2" value="إنضم" onClick={handleSubmit}/>
                        </div>
                        <TxtField align='right' color='white' className="flex-1 mr-2" label="البريد الإلكتروني" onChange={handleEmailChange} />
                        <TxtField align='right' color='white' className="flex-1" label="رقم الهاتف" onChange={handleNumChange} />
                    </div>
                </div>
            </div>
        </div>
    )

    return content
}

export default MakerRegist
